function showPage(page){const content=document.getElementById('content');if(page==='home'){content.innerHTML=`
            <h2>Welcome!</h2>
            <p>This is a simple PHP SPA demo.</p>
            <button onclick="loadData()">Load API Data</button>
            <div id="result"></div>
        `}else if(page==='about'){content.innerHTML=`
            <h2>About</h2>
            <p>This demo uses:</p>
            <ul>
                <li>PHP for backend</li>
                <li>Vanilla JavaScript</li>
                <li>Simple CSS</li>
                <li>Docker & Nginx</li>
            </ul>
        `}}
function loadData(){fetch('/api/data').then(response=>response.json()).then(data=>{document.getElementById('result').innerHTML=`<strong>${data.message}</strong><br>Time: ${data.time}`;document.getElementById('result').style.display='block'}).catch(error=>{document.getElementById('result').innerHTML='Error loading data';document.getElementById('result').style.display='block'})}